INSERT INTO `jos_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES (3,86,'Home','home','<h3>A Burning Man Theme Camp</h3>\r\n<p>We are The Mycodelic Forest, a Burning Man theme camp located around the 3\'oclock sector in Black Rock City.&nbsp;</p>\r\n<p dir=\"ltr\">At the Mycodelic Forest the magic of renewal awaits you! We are inspired by the special ability of mycelium to breakdown organic compounds and decompose biomass into compost to create new life. In this way we strive to break down barriers and educate others on how to protect the playa and the Burner Culture to create an emotionally fertile environment for everyone.</p>\r\n<p>Our group has been attending Burning Man as The Mycodelic Forest for the past 4 years, but spent a year before that as The Enchanted Forest Funki. This website is a place to learn about all things related to the Mycodelic Forest&nbsp;Burning Man theme camp and to organize and coordinate our members. If you would like to contribute, in any form, to the Mycodelic Forest, please proceed to our <a href=\"index.php?Itemid=423\">forum</a> to discuss anything camp related.</p>\r\n<p><a href=\"index.php?option=com_content&amp;view=article&amp;id=4&amp;Itemid=147\" class=\"readon\">Read More</a></p>','',1,11,'2014-09-25 01:10:37',802,'','2017-05-25 07:56:58',43,0,'0000-00-00 00:00:00','2014-09-25 01:10:37','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\",\"extra-class\":\"\"}',24,4,'','',1,211808,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','');
INSERT INTO `jos_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES (4,87,'About Us','about-us','<p>We are The Mycodelic Forest, a Burning Man theme camp located around the 3\'oclock sector in Black Rock City.&nbsp;</p>\r\n<p>The Mycodelic Forest is a family of spontaneous and vigorously creative people. At all times, we are a peaceful, tolerant family with respect for life and the environment. Burning Man could not exist without the love and devotion of each entity and we try to inspire, by being inspired. Each person is responsible for everything that is them, and through what\'s uniquely realized, we want to provide magic; of thought, of creation, and of evolution. We want everyone to be proud of what a community filled with responsibility, love and acceptance can create. We encourage immediacy of experience. Be here, be now. Join us in the Mycodelic Forest.</p>\r\n<p>We are a group of dedicated, civic minded burners that believe in the interconnectedness of all organisms. Our group has been attending Burning Man as The Mycodelic Forest for the past 2 years, but spent a year before that as The Enchanted Forest Funki. This website is a place to learn about all things related to the Mycodelic Forest&nbsp;Burning Man theme camp and to organize and coordinate our members. If you would like to contribute, in any form, to the Mycodelic Forest, please proceed to our <a href=\"index.php?Itemid=423\">forum</a> to discuss anything camp related.</p>','',1,11,'2014-09-25 01:10:48',802,'','2016-03-28 03:44:06',43,0,'0000-00-00 00:00:00','2014-09-25 01:10:48','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_i