INSERT INTO `jos_kunena_topics` (`id`, `category_id`, `subject`, `icon_id`, `label_id`, `locked`, `hold`, `ordering`, `posts`, `hits`, `attachments`, `poll_id`, `moved_id`, `first_post_id`, `first_post_time`, `first_post_userid`, `first_post_message`, `first_post_guest_name`, `last_post_id`, `last_post_time`, `last_post_userid`, `last_post_message`, `last_post_guest_name`, `rating`, `params`) VALUES (15,8,'Homepage Content',0,0,0,0,0,4,2,0,0,0,28,1459135853,43,'Current Homepage content:\r\n\r\n[quote]We are The Mycodelic Forest, a Burning Man theme camp located around the 3\'oclock sector in Black Rock City. \r\n\r\nWe are a group of dedicated, civic minded burners that believe in the interconnectedness of all organisms. Our group has been attending Burning Man as The Mycodelic Forest for the past 2 years, but spent a year before that as The Enchanted Forest Funki.\r\n\r\nThis website is a place to learn about all things related to the Mycodelic Forest Burning Man theme camp and to organize and coordinate our members. If you would like to contribute, in any form, to the Mycodelic Forest, please proceed to our forum to discuss anything camp related.[/quote]','trevorbice',31,1459137169,43,'test','experthumanpilot',0,'');
