INSERT INTO `jos_kunena_topics` (`id`, `category_id`, `subject`, `icon_id`, `label_id`, `locked`, `hold`, `ordering`, `posts`, `hits`, `attachments`, `poll_id`, `moved_id`, `first_post_id`, `first_post_time`, `first_post_userid`, `first_post_message`, `first_post_guest_name`, `last_post_id`, `last_post_time`, `last_post_userid`, `last_post_message`, `last_post_guest_name`, `rating`, `params`) VALUES (31,3,'Proposed Group Photo Times',0,0,0,0,0,1,389,0,0,0,73,1470793458,43,'So we\'ve been a little haphazard about this previous years, so this year I want to make sure everyone is on the same page about when and where we will be doing group photos. I am proposing we have a few pre-designated times, that way even if we miss one we can still be on others!\r\n\r\nMonday: 4:00 PM?\r\n??\r\nPre-Man Burn?\r\nPre-Temple Burn?\r\n\r\nAny other suggestions?\r\n\r\nWe also need to make sure we do a good job of documenting the camp this year!','experthumanpilot',73,1470793458,43,'So we\'ve been a little haphazard about this previous years, so this year I want to make sure everyone is on the same page about when and where we will be doing group photos. I am proposing we have a few pre-designated times, that way even if we miss one we can still be on others!\r\n\r\nMonday: 4:00 PM?\r\n??\r\nPre-Man Burn?\r\nPre-Temple Burn?\r\n\r\nAny other suggestions?\r\n\r\nWe also need to make sure we do a good job of documenting the camp this year!','experthumanpilot',0,'');
INSERT INTO `jos_kunena_topics` (`id`, `category_id`, `subject`, `icon_id`, `label_id`, `locked`, `hold`, `ordering`, `posts`, `hits`, `attachments`, `poll_id`, `moved_id`, `first_post_id`, `first_post_time`, `first_post_userid`, `first_post_message`, `first_post_guest_name`, `last_post_id`, `last_post_time`, `last_post_userid`, `last_post_message`, `last_post_guest_name`, `rating`, `params`) VALUES (323,13,'DGS Tickets 2017',0,0,0,0,0,2,11,0,0,0,366,1487585671,43,'Also, just a reminder we need members able to purchase DGS  (Directed Group Sale) tickets for Core Camp members. Please message me if you are willing to help set up / break down / early arrival. We are asking members who are willing to do DGS to buy two tickets to ensure all our Team can get to the Playa. This gives you much better odds of getting a ticket, but comes with some responsibilities!\r\n\r\nWe need to get registered by February 24th!','Trailblazer',379,1488227339,43,'So there were a few glitches with getting our all the DGS ticket invites, but at this point I think we have four people able to purchase tickets. That would be Me, Rachael, Kate, and Malachi. This will get us 8 tickets.\r\n\r\nMy extra ticket will be going to Alex Newman who has said he could be part of the core team.','Trailblazer',0,'');
