INSERT INTO `jos_ucm_history` (`version_id`, `ucm_item_id`, `ucm_type_id`, `version_note`, `save_date`, `editor_user_id`, `character_count`, `sha1_hash`, `version_data`, `keep_forever`) VALUES (57,3,1,'','2016-04-09 03:07:16',43,3113,'cfc0ab8844bf74d39b89bf4737db47736e8b8d71','{\"id\":3,\"asset_id\":\"86\",\"title\":\"Home\",\"alias\":\"home\",\"introtext\":\"<h3>A Burning Man Theme Camp<\\/h3>\\r\\n<p>We are The Mycodelic Forest, a Burning Man theme camp located around the 3\'oclock sector in Black Rock City.&nbsp;<\\/p>\\r\\n<p>We are a family of spontaneous and vigorously creative people. At all times, we are a peaceful, tolerant family with respect for life and the environment. Burning Man could not exist without the love and devotion of each entity. We try to inspire others by being inspired ourselves. Each person is responsible for everything that is them, and through what\'s uniquely realized, we want to provide magic; of thought, of creation, and of evolution. We want everyone to be proud of what a community filled with responsibility, love and acceptance can create. We encourage immediacy of experience. Be here, be now. Join us in the Mycodelic Forest.<\\/p>\\r\\n<p>Our group has been attending Burning Man as The Mycodelic Forest for the past 2 years, but spent a year before that as The Enchanted Forest Funki. This website is a place to learn about all things related to the Mycodelic Forest&nbsp;Burning Man theme camp and to organize and coordinate our members. If you would like to contribute, in any form, to the Mycodelic Forest, please proceed to our <a href=\\\"index.php?Itemid=423\\\">forum<\\/a> to discuss anything camp related.<\\/p>\\r\\n<p><a href=\\\"index.php?option=com_content&amp;view=article&amp;id=4&amp;Itemid=147\\\" class=\\\"readon\\\">Read More<\\/a><\\/p>\",\"fulltext\":\"\",\"state\":1,\"catid\":\"11\",\"created\":\"2014-09-25 01:10:37\",\"created_by\":\"802\",\"created_by_alias\":\"\",\"modified\":\"2016-04-09 03:07:16\",\"modified_by\":\"43\",\"checked_out\":\"43\",\"checked_out_time\":\"2016-04-09 03:05:05\",\"publish_up\":\"2014-09-25 01:10:37\",\"publish_down\":\"0000-00-00 00:00:00\",\"images\":\"{\\\"image_intro\\\":\\\"\\\",\\\"float_intro\\\":\\\"\\\",\\\"image_intro_alt\\\":\\\"\\\",\\\"image_intro_caption\\\":\\\"\\\",\\\"image_fulltext\\\":\\\"\\\",\\\"float_fulltext\\\":\\\"\\\",\\\"image_fulltext_alt\\\":\\\"\\\",\\\"image_fulltext_caption\\\":\\\"\\\"}\",\"urls\":\"{\\\"urla\\\":false,\\\"urlatext\\\":\\\"\\\",\\\"targeta\\\":\\\"\\\",\\\"urlb\\\":false,\\\"urlbtext\\\":\\\"\\\",\\\"targetb\\\":\\\"\\\",\\\"urlc\\\":false,\\\"urlctext\\\":\\\"\\\",\\\"targetc\\\":\\\"\\\"}\",\"attribs\":\"{\\\"show_title\\\":\\\"\\\",\\\"link_titles\\\":\\\"\\\",\\\"show_tags\\\":\\\"\\\",\\\"show_intro\\\":\\\"\\\",\\\"info_block_position\\\":\\\"\\\",\\\"show_category\\\":\\\"\\\",\\\"link_category\\\":\\\"\\\",\\\"show_parent_category\\\":\\\"\\\",\\\"link_parent_category\\\":\\\"\\\",\\\"show_author\\\":\\\"\\\",\\\"link_author\\\":\\\"\\\",\\\"show_create_date\\\":\\\"\\\",\\\"show_modify_date\\\":\\\"\\\",\\\"show_publish_date\\\":\\\"\\\",\\\"show_item_navigation\\\":\\\"\\\",\\\"show_icons\\\":\\\"\\\",\\\"show_print_icon\\\":\\\"\\\",\\\"show_email_icon\\\":\\\"\\\",\\\"show_vote\\\":\\\"\\\",\\\"show_hits\\\":\\\"\\\",\\\"show_noauth\\\":\\\"\\\",\\\"urls_position\\\":\\\"\\\",\\\"alternative_readmore\\\":\\\"\\\",\\\"article_layout\\\":\\\"\\\",\\\"show_publishing_options\\\":\\\"\\\",\\\"show_article_options\\\":\\\"\\\",\\\"show_urls_images_backend\\\":\\\"\\\",\\\"show_urls_images_frontend\\\":\\\"\\\",\\\"extra-class\\\":\\\"\\\"}\",\"version\":23,\"ordering\":\"4\",\"metakey\":\"\",\"metadesc\":\"\",\"access\":\"1\",\"hits\":\"6719\",\"metadata\":\"{\\\"robots\\\":\\\"\\\",\\\"author\\\":\\\"\\\",\\\"rights\\\":\\\"\\\",\\\"xreference\\\":\\\"\\\"}\",\"featured\":\"0\",\"language\":\"*\",\"xreference\":\"\"}',0);
INSERT INTO `jos_ucm_history` (`version_id`, `ucm_item_id`, `ucm_type_id`, `version_note`, `save_date`, `editor_user_id`, `character_count`, `sha1_hash`, `version_data`, `keep_forever`) VALUES (58,12,6,'','2016-09-08 03:40:11',43,3542,'275ffc82f1bab79add53b161c44e8c7820e00214','{\"id\":12,\"asset_id\":196,\"parent_id\":\"1\",\"lft\":\"21\",\"rgt\":22,\"level\":1,\"path\":null,\"extension\":\"com_content\",\"title\":\"The 10 Principals\",\"alias\":\"the-10-principals\",\"note\":\"\",\"description\":\"<p><strong>Radical Inclusion<\\/strong><br \\/>Anyone may be a part of Burning Man. We welcome and respect the stranger. No prerequisites exist for participation in our community.<\\/p>\\r\\n<p><strong>Gifting<\\/strong><br \\/>Burning Man is devoted to acts of gift giving. The value of a gift is unconditional. Gifting does not contemplate a return or an exchange for something of equal value.<\\/p>\\r\\n<p><strong>Decommodification<\\/strong><br \\/>In order to preserve the spirit of gifting, our community seeks to create social environments that are unmediated by commercial sponsorships, transactions, or advertising. We stand ready to protect our culture from such exploitation. We resist the substitution of consumption for participatory experience.<\\/p>\\r\\n<p><strong>Radical Self-reliance<\\/strong><br \\/>Burning Man encourages the individual to discover, exercise and rely on his or her inner resources.<\\/p>\\r\\n<p><strong>Radical Self-expression<\\/strong><br \\/>Radical self-expression arises from the unique gifts of the individual. No one other than the individual or a collaborating group can determine its content. It is offered as a gift to others. In this spirit, the giver should respect the rights and liberties of the recipient.<\\/p>\\r\\n<p><strong>Communal Effort<\\/strong><br \\/>Our community values creative cooperation and collaboration. We strive to produce, promote and protect social networks, public spaces, works of art, and methods of communication that support such interaction.<\\/p>\\r\\n<p><strong>Civic Responsibility<\\/strong><br \\/>We value civil society. Community members who organize events should assume responsibility for public welfare and endeavor to communicate civic responsibilities to participants. They must also assume responsibility for conducting events in accordance with local, state and federal laws.<\\/p>\\r\\n<p><strong>Leaving No Trace<\\/strong><br \\/>Our community respects the environment. We are committed to leaving no physical trace of our activities wherever we gather. We clean up after ourselves and endeavor, whenever possible, to leave such places in a better state than when we found them.<\\/p>\\r\\n<p><strong>Participation<\\/strong><br \\/>Our community is committed to a radically participatory ethic. We believe that transformative change, whether in the individual or in society, can occur only through the medium of deeply personal participation. We achieve being through doing. Everyone is invited to work. Everyone is invited to play. We make the world real through actions that open the heart.<\\/p>\\r\\n<p><strong>Immediacy<\\/strong><br \\/>Immediate experience is, in many ways, the most important touchstone of value in our culture. We seek to overcome barriers that stand between us and a recognition of our inner selves, the reality of those around us, participation in society, and contact with a natural world exceeding human powers. No idea can substitute for this experience.<\\/p>\",\"published\":\"1\",\"checked_out\":null,\"checked_out_time\":null,\"access\":\"1\",\"params\":\"{\\\"category_layout\\\":\\\"\\\",\\\"image\\\":\\\"\\\",\\\"image_alt\\\":\\\"\\\",\\\"t3_extrafields\\\":\\\"\\\"}\",\"metadesc\":\"\",\"metakey\":\"\",\"metadata\":\"{\\\"author\\\":\\\"\\\",\\\"robots\\\":\\\"\\\"}\",\"created_user_id\":\"43\",\"created_time\":\"2016-09-08 03:40:11\",\"modified_user_id\":null,\"modified_time\":\"2016-09-08 03:40:11\",\"hits\":\"0\",\"language\":\"*\",\"version\":null}',0);
INSERT INTO `jos_ucm_history` (`version_id`, `ucm_item_id`, `ucm_type_id`, `version_note`, `save_date`, `editor_user_id`, `character_count`, `sha1_hash`, `version_data`, `keep_forever`) VALUES (59,12,6,'','2016-09-08 03:44:36',43,973,'dae6d99aa1f40c8130e7a51e51ae41b4afc1226b','{\"id\":12,\"asset_id\":\"196\",\"parent_id\":\"1\",\"lft\":\"21\",\"rgt\":\"22\",\"level\":\"1\",\"path\":\"the-10-principals\",\"extension\":\"com_content\",\"title\":\"The 10 Principles of Burning Man\",\"alias\":\"the-10-principals\",\"note\":\"\",\"description\":\"<p>Burning Man co-founder Larry Harvey wrote the Ten Principles in 2004 as guidelines for the newly-formed Regional Network. They were crafted not as a dictate of how people should be and act, but as a reflection of the community\\u2019s ethos and culture as it had organically developed since the event\\u2019s inception.<\\/p>\",\"published\":\"1\",\"checked_out\":\"43\",\"checked_out_time\":\"2016-09-08 03:40:58\",\"access\":\"1\",\"params\":\"{\\\"category_layout\\\":\\\"\\\",\\\"image\\\":\\\"\\\",\\\"image_alt\\\":\\\"\\\",\\\"t3_extrafields\\\":\\\"\\\"}\",\"metadesc\":\"\",\"metakey\":\"\",\"metadata\":\"{\\\"author\\\":\\\"\\\",\\\"robots\\\":\\\"\\\"}\",\"created_user_id\":\"43\",\"created_time\":\"2016-09-08 03:40:11\",\"modified_user_id\":\"43\",\"modified_time\":\"2016-09-08 03:44:36\",\"hits\":\"0\",\"language\":\"*\",\"version\":\"1\"}',0);
